import React, {useState} from 'react';
import {Button, Modal} from 'react-bootstrap';
import {List, ListItem, ListItemAvatar,Avatar,ListItemText} from '@material-ui/core'

export default function QuestInfoBtn() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const quest_info = {
    title: 'The Great Novigrad Heist',
    description: 'Free the witches',
    nodes: [
      {
        title: 'start',
        description: 'start it',
        date: Date(Date.now()).toString(),
        isComplete: true
      },
      {
        title: 'middle',
        description: 'middle part',
        date: Date(Date.now()).toString(),
        isComplete: true
      },
      {
        title: 'end',
        description: 'The last part',
        date: Date(Date.now()).toString(),
        isComplete: false
      }
    ],
    mentor: "John",
    date_finish: Date(Date.now()).toString()
  };

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Quest Info
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{quest_info.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{quest_info.title}</p>
          <p>{quest_info.description}</p>
          <List>
          {
            quest_info.nodes.map((node, index)=>(
              <ListItem>
                <ListItemAvatar>
                  <Avatar>
                    <img src="https://www.pinclipart.com/picdir/middle/379-3797946_software-developer-computer-servers-web-others-web-developer.png" alt="party-image" />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText primary={node.title} secondary={node.description} />
              </ListItem>
            ))
          }
          <p>{quest_info.mentor}</p>
          <p>{quest_info.date_finish}</p>
          </List>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
